// IF_ELSE
var age = 5;
if (age > 7) {
    console.log("Sap vao tieu hoc");
}
else {
    console.log("Van hoc mau giao");
}
// Note : Khi co nhieu dieu kien thi nen su dung else if
// SWITCH CASE
var ageVa = 20;
switch (ageVa) {
    case 20:
    case 21:
        console.log("sap lay vo roi");
        break;
    default:
        console.log("Loi tum lum");
        break;
}
